DROP DATABASE IF EXISTS twitch_padatables;
CREATE DATABASE twitch_padatables CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
USE twitch_padatables;

CREATE TABLE peliculas(
  ID SERIAL PRIMARY KEY,
  NOMBRE VARCHAR(100) NOT NULL,
  SINOPSIS TEXT,
  ANIO YEAR,
  GENERO VARCHAR(100),
  DIRECTOR VARCHAR(100),
  PUNTAJE FLOAT( 4,2 )
);

